import React from 'react';
import CP from '../imgs/CP.webp';
import TS from '../imgs/travis.jpg';

const Card = () => {
  let cardArray = [];

  const createCard = (src, text, price) => (
    <div className="card">
      <div className="card-container">
        <img className="card-image" src={src} alt="card-img" />
        <div className="card-text-container">
          <h3 className="card-name">{text}</h3>
          <p className="card-price">{`$${price}`}</p>
        </div>
      </div>
    </div>
  );

  const common = createCard(CP, 'Common Projects', '305');
  const trav = createCard(TS, "Travis Scott's", '2500');

  cardArray.push(common, trav);

  return cardArray;
};

export default Card;
